from web3 import Web3

from config import URL, API_KEY

ETH_USD_ADDRESS = "0x5f4ec3df9cbd43714fe2740f5e3616155c5b8419"
LINK_ETH_ADDRESS = "0xdc530d9457755926550b59e8eccdae7624181557"
USDT_USD_ADDRESS = "0x3e7d1eab13ad0104d2750b8863b489d65364e32d"


def get_fresh_addresses(with_print=True):
    # Change this to use your own RPC URL
    web3 = Web3(Web3.HTTPProvider(URL + '/' + API_KEY))

    if not web3.isConnected():
        print(f"can't connect to {URL}")
        return

